import { BigNumber } from "@ethersproject/bignumber";
export function parseBigNumberish(n, token, chainId) {
    return n instanceof BigNumber
        ? n
        : token.etherToWei(n, chainId);
}
export function parseLPTokenBigNumberishArray(lpToken, amounts, chainId) {
    var poolTokens = lpToken.poolTokens;
    return amounts.map(function (n, idx) {
        return parseBigNumberish(n, poolTokens[idx], chainId);
    });
}
export function parseApproveAmount(amount, token, chainId) {
    return amount
        ? parseBigNumberish(amount, token, chainId)
        : undefined;
}
export function logError(e) {
    var err = e instanceof Error ? e : new Error(e);
    console.error(err);
}
